            sb.AppendFormat("&formhash={0}", "69256b23");
            sb.AppendFormat("&voteId={0}", "10879");
            sb.AppendFormat("&type={0}", "html");
            sb.AppendFormat("language={0}", "zh_CN");
            //sb.AppendFormat("retUrl={0}");
            sb.AppendFormat("&items_119748={0}", info.items_119748);
            sb.AppendFormat("&items_119749={0}", info.items_119749);
            sb.AppendFormat("&items_119750={0}", info.items_119750);
            sb.AppendFormat("&items_119751={0}", info.items_119751);
            sb.AppendFormat("&items_119752={0}", info.items_119752);
            //string result = Patch.Core.Web.HttpHelper.GetWebRequest(url, sb.ToString(), "POST", Encoding.UTF8);

            string result = "投票成功";

            //if (i == 2 || i == 3)
            //{
            //    result = "";
            //}

            if (result.Contains("投票成功"))
            {
                result = string.Format("当前执行第到{0}条，{1}，执行结果成功<br/>", i, info.xingming);
                WirteTxt(string.Format("{0}|{1},", info.xingming, info.idCard), "log/yes.txt", false);
            }
            else
            {
                WirteTxt(string.Format("{0},{1}", info.xingming, result), "log/error.txt", true);
                result = string.Format("当前执行第到{0}条，{1}，执行结果失败<br/>", i, info.xingming);
                WirteTxt(string.Format("{0}|{1},", info.xingming, info.idCard), "log/no.txt", false);
            }

            Response.Write(result);
            WirteTxt(result.Replace("<br/>", ""), "log/all.txt", true);

            if (i == list.Count - 1)
            {
                Response.Write(string.Format("执行完成，共执行{0}条记录<br/>", i)); return;
            }
            else
            {
                url = string.Format("<script>location.href='FormPost.aspx?i={0}';</script>", ++i);

                Response.Write(url);
            }
        }

